def graphWaveform(signal, sr):    
    trace1 = {
      "mode": "lines", 
      "name": "Signal with last point", 
      "type": "scatter", 
      "x":[1/sr*pos for pos in range(len(signal))],
      "y":signal,
    }
    layout = {
      "title": "Waveform Audio Signal", 
      "xaxis": {
        "title": "Time (s)", 
        "titlefont": {
          "size": 14, 
          "color": "black", 
          "family": "Courier New, monospace"
        }
      }, 
      "yaxis": {
        "title": "Amp", 
        "titlefont": {
          "size": 14, 
          "color": "black", 
          "family": "Courier New, monospace"
        }
      }, 
      "legend": {
        "font": {
          "size": 12, 
          "color": "#000", 
          "family": "sans-serif"
        }, 
        "bgcolor": "#FFFFFF", 
        "traceorder": "normal", 
        "bordercolor": "#FFFFFF", 
        "borderwidth": 2
      }
    }
    fig = go.Figure(data = [trace1], layout = layout,)
    fig.show()
