tipo = ""

if (abs(ladoB - ladoC ) < ladoA) and (ladoA < (ladoB + ladoC)):
    triangulo = 1
elif (abs(ladoA - ladoC) < ladoB) and (ladoB < (ladoA + ladoC)):
    triangulo = 1
elif (abs(ladoA - ladoB) < ladoC) and (ladoC < (ladoA + ladoB)):
    triangulo = 1
else:
    triangulo = 0

if (triangulo == 1):
    if ((ladoA == ladoB) and (ladoB == ladoC)):
