class Grid {
    public:
        int nrows = 0;
        int ncols = 0;
        int **mat = NULL;

        Grid() {}

        Grid(const Grid& grid) {
            gridCopy(grid);
        }

        void gridCopy(const Grid& grid) {
            nrows = grid.nrows;
            ncols = grid.ncols;

            mat = new int*[nrows];
            for (int i = 0; i < nrows; ++i) {
                mat[i] = new int[ncols];
                for (int j = 0; j < ncols; ++j)
                    mat[i][j] = grid.mat[i][j];
            }
        }

        Grid(int nr, int nc) {
            gridBySize(nr,nc);
        }

        void gridBySize(int nr, int nc) {
            nrows = nr;
            ncols = nc;

            mat = new int*[nrows];
            for (int i = 0; i < nrows; ++i) {
                mat[i] = new int[ncols];
                for (int j = 0; j < ncols; ++j)
                    mat[i][j] = -1;
            }
        }

        Grid(std::vector<std::vector<int>> &rows) {
            nrows = rows.size();
            ncols = rows[0].size();

            mat = new int*[nrows];
            for (int i = 0; i < nrows; ++i) {
                mat[i] = new int[ncols];
                for (int j = 0; j < ncols; ++j)
                    mat[i][j] = rows[i][j];
            }
        }

        void print() {
            std::cout << "\n";
            for (int i = 0; i < nrows; ++i) {
                for (int j = 0; j < ncols; ++j) {
                    if (mat[i][j] == 0)
                        std::cout << '.';
                    else
                        std::cout << mat[i][j];
                }
                std::cout << "\n";
            }
        }

        ~Grid() {
            clear();
        }

        virtual void clear() {
            if (mat) {
                for (int i = 0; i < nrows; ++i)
                    delete mat[i];
                delete mat;
            }
            mat = NULL;
        }

        bool operator== (const Grid &ref) const
        {
            if ((nrows != ref.nrows) | (ncols != ref.ncols))
                return false;
            for (int i = 0; i < nrows; ++i)
                for (int j = 0; j < ncols; ++j)
                    if (mat[i][j] != ref.mat[i][j])
                        return false;
            return true;
        }

        std::string flatten() {
            std::string out;
            for (int i = 0; i < nrows; ++i) {
                out.append("|");
                for (int j = 0; j < ncols; ++j)
                    out.append(std::to_string(mat[i][j]));
            }
            out.append("|");
            return out;
        }
};

