class PropModulo : public Property {
    public:

        PropModulo(int mod, bool is_row, bool is_col, bool is_rev) : mod{mod}, is_row{is_row}, is_col{is_col}, is_rev(is_rev) {
            name = "PropModulo";
            name += to_string(mod);
            if (is_row)
                name += "T";
            else
                name += "F";
            if (is_col)
                name += "T";
            else
                name += "F";
            if (is_rev)
                name += "R";
        }

        virtual void populate(Grid *grid) {
            gridCopy(*grid);

            for (int i = 0; i < nrows; ++i)
                for (int j = 0; j < ncols; ++j) {
                    mat[i][j] = 0;
                    int x = i;
                    int y = j;
                    if (is_rev) {
                        x = nrows-1-x;
                        y = ncols-1-y;
                    }
                    if (is_row)
                        mat[i][j] += mod*(x%mod);
                    if (is_col)
                        mat[i][j] += (y%mod);
                }
        }

    private:
        int mod = 2;
        bool is_row = true;
        bool is_col = true;
        bool is_rev = false;
};



