Task* parse_file(std::string filepath) {

    std::ifstream ifs;
    ifs.open(filepath.c_str(), std::ifstream::in);

    int cnt = 0;
    bool char_t = false;
    bool char_n = false;
    bool is_train = false;
    bool is_test = false;
    bool is_input = false;
    bool is_output = false;
    int opened = 0;
    int opened_curl = 0;

    std::vector<int> row;
    std::vector<std::vector<int>> rows;

    std::vector<Pair> pairs;
    Pair *pair = NULL;
    Task *task = new Task();

    char c = ifs.get();
    while (ifs.good()) {

        if ((c == 'e') & char_t)
            is_test = true;
        if ((c == 'r') & char_t)
            is_train = true;

        if ((c == 'p') & char_n)
            is_input = true;
        if ((c == 'p') & char_t)
            is_output = true;

        char_t = (c == 't');
        char_n = (c == 'n');

        if (c == '{') {
            opened_curl++;
            if (opened_curl == 2)
                pair = new Pair();
        }
        if (c == '}') {
            opened_curl--;
        }

        if (c == '[') {
            opened++;
        }
        if (c == ']') {
            opened--;
            if (opened == 0) {
                is_test = false;
                is_train = false;
            }
            if (opened == 1) {
                Grid *grid = new Grid(rows);

                if (is_input) {
                    pair->set_input(grid);

                    if (is_train)
                        task->add_train(pair);
                    if (is_test)
                        task->add_test(pair);
                }
                if (is_output) {
                    pair->set_output(grid);
                }

                rows.clear();

                is_input = false;
                is_output = false;
            }
            if (opened == 2) {
                rows.push_back(row);
                row.clear();
            }
        }

        if ((c >= '0') & (c <= '9')) {
            row.push_back(int(c) - int('0'));
        }

        c = ifs.get();
        cnt++;
    }

    ifs.close();

    return task;
}

