#本当はlambda がいいけど、pythonにはlambda関数があるからNUMBERにします。


def func(num): #Box-Cox変換
    if NUMBER:
        return ((num+1)**(NUMBER)-1)/NUMBER
    else:
        return (np.log10(num+1))


def func_inv(num): #逆変換
    if num*NUMBER >0 :
        return (num*NUMBER+1)**(1/NUMBER) -1
    elif NUMBER == 0:
        return (10**num -1)
    elif num == 0:
        return 0
    else:
        if NUMBER > 0:#numが負はありえないですが、predictで出るかも。NUMBERの正負によって場合分けしておく
            return 0
        else:
            return 10**6

vfunc = np.vectorize(func)
vfunc_inv = np.vectorize(func_inv)
