def estParfait(n):
    somme_diviseurs = 0
    for i in range(1, n):
        if n % i == 0:
            somme_diviseurs += i
    return somme_diviseurs == n

def estPremier(n):
    cmp=0
    for j in range(2,n):
        if n%j ==0 : cmp=1
    return cmp==0

def parfiat_premier(nbr): 
    result = []
    for i in range(1, nbr):
        if estParfait(i) and estPremier(i):
            result.append(i)
    return result

