# get the synomyms of the word 'mot_cle'

def get_synonymes(mot_cle):
    lemma = set()
    for ss in wordnet.synsets(mot_cle):
        for w in ss.lemma_names():
            index = ss.name().find('.')+1

            if ss.name()[index] == 'n': 
                lemma.add(w.lower().replace('_',' '))
    return lemma

