def alarmaHumedadTemperatura(temperatura, humedad):
    if temperatura < 40 and humedad >= 70:
        return "ADVERTENCIA"
    elif temperatura >= 50 or humedad >= 60:
        return "CRITICO"
    else:
        return "NORMAL"

print("Se definió la función alarmaHumedadTemperatura(temperatura, humedad)")
