N=4;
def alpha(n):
    if n==0:
        return np.sqrt(1/N)
    else:
        return np.sqrt(2/N)

F=np.zeros([N,N,N,N])
for k in range(N):
     for l in range(N):
         for m in range(N):
             for n in range(N):
                 F[k][l][m][n]=np.sum(alpha(k)*alpha(l)*(cos(((2*m+l)*3.14*k)/(2*N)))*(cos(((2*n+1)*3.14*l)/(2*N))))

count=1;
for k in range(N):
    for l in range(N):
        plt.subplot(N,N,count)
        plt.imshow(F[k][l],cmap='gray'),plt.xticks([]),plt.yticks([])
        count=count+1
