class Metric:
    def __init__(self):
        self.sse = 0
        self.num_samples = 0

    def update(self, targets, predictions):
        self.sse += np.sum(np.square(targets - predictions))
        self.num_samples += len(targets)

    def get_rmse(self):
        rmse = np.sqrt(self.sse / self.num_samples)
        return rmse

