def my_agent(obs, config):

    ################################
    # Imports and helper functions #
    ################################

    import numpy as np
    import random

    # Gets board at next step if agent drops piece in selected column
    def drop_piece(grid, col, piece, config):
        next_grid = grid.copy()
        for row in range(config.rows-1, -1, -1):
            if next_grid[row][col] == 0:
                break
        next_grid[row][col] = piece
        return next_grid

    # Returns True if dropping piece in column results in game win
    def check_winning_move(obs, config, col, piece):
        # Convert the board to a 2D grid
        grid = np.asarray(obs.board).reshape(config.rows, config.columns)
        next_grid = drop_piece(grid, col, piece, config)
        # horizontal
        for row in range(config.rows):
            for col in range(config.columns-(config.inarow-1)):
                window = list(next_grid[row,col:col+config.inarow])
                if window.count(piece) == config.inarow:
                    return True
        # vertical
        for row in range(config.rows-(config.inarow-1)):
            for col in range(config.columns):
                window = list(next_grid[row:row+config.inarow,col])
                if window.count(piece) == config.inarow:
                    return True
        # positive diagonal
        for row in range(config.rows-(config.inarow-1)):
            for col in range(config.columns-(config.inarow-1)):
                window = list(next_grid[range(row, row+config.inarow), range(col, col+config.inarow)])
                if window.count(piece) == config.inarow:
                    return True
        # negative diagonal
        for row in range(config.inarow-1, config.rows):
            for col in range(config.columns-(config.inarow-1)):
                window = list(next_grid[range(row, row-config.inarow, -1), range(col, col+config.inarow)])
                if window.count(piece) == config.inarow:
                    return True
        return False

    #########################
    # Agent makes selection #
    #########################

    valid_moves = [col for col in range(config.columns) if obs.board[col] == 0]

    agent_piece = obs.mark
    opponent_piece = (agent_piece%2) + 1 #if agent_piece == 1 opponen_piece == 2 and vice-versa
    for col in valid_moves:
        #checks if winning move for agent exists
        if check_winning_move(obs, config, col, agent_piece):
            return col
        #else checks if winning move for opponent exists so agent can block
        if check_winning_move(obs, config, col, opponent_piece):
            return col

    #if a winning move for neither agent nor opponent exists, agent selects random move(col)
    return random.choice(valid_moves)
