def age_d(x):
    if x>30:
        return 'Above 30'
    if x<=30 and x>25:
        return 'Between 25-30'
    if x<=25 and x>20:
        return 'Between 20-25'
    else:
        return 'Below 20'

data['Age_dist']=data.Age.apply(age_d)

