def array_equal(array_1, array_2):
    """
    custom function to compare two 1D np arrays.
    """
    if array_1.shape != array_2.shape:
        return False

    for i in range(len(array_1)):
        if (array_1[i] is array_2[i]) or (array_1[i] == array_2[i]):
            continue 
        return False
    return True

