#getting filepath from study_id or image_id
def get_path(file_id,main_path,id_type):
    name = file_id.split("_")[0]
    if id_type == "study":
        path = tf.io.gfile.glob(main_path+f"/{name}/*/*.dcm")[0]
    else:
        path = tf.io.gfile.glob(main_path+f"/*/*/{name}.dcm")[0]
    return path
