import plotly.graph_objs as go

#Creating trace1
trace1 = {
            'x' : df2014.university_name,
            'y' : df2014.citations,
            'name' : 'citation',
            'type' : 'bar'
};
trace2 = {
            'x' : df2014.university_name,
            'y' : df2014.teaching,
            'name' : 'teaching',
            'type' : 'bar'
};
data = [trace1,trace2]
layout = {
  'xaxis': {'title': 'Top 3 universities'},
  'barmode': 'relative',
  'title': 'citations and teaching of top 3 universities in 2014'
};
fig = go.Figure(data = data, layout = layout)
iplot(fig)
