def find_highest_crime_city(df, citylist):
    max_city = ''
    max_crime = 0
    for city in citylist:
        total_crime = df.loc[df.City == city, 'Criminal Cases'].sum()
        if total_crime > max_crime:
            max_city = city
            max_crime = total_crime

    return max_city, max_crime
