outlier_feature=[]
no_outlier_feature=[]
for i in train.columns:
    IQR =train[i].quantile(.75) -train[i].quantile(.25)
    upperlimit =train[i].quantile(.75) +(1.5*IQR)
    lowerlimit =train[i].quantile(.25) -(1.5*IQR)
    if (train[i].max() > upperlimit) | (train[i].min() < lowerlimit):
        outlier_feature.append(i)
    else:
        no_outlier_feature.append(i)
