# define function for categorization of rating

def rating_category(rating):
    rating = int(rating)

    bracket = ''
    if rating in range (1, 3):
        bracket = "poor"
    if rating in range (3, 4):
        bracket = "fair"
    if rating in range (4, 6):
        bracket = "good"
    return bracket

