import string
def remove_punct(text):
    table=str.maketrans('','',string.punctuation)
    return text.translate(table)

df_train['text_cleaned']=df_train['text_cleaned'].apply(lambda x : remove_punct(x))
df_test['text_cleaned']=df_test['text_cleaned'].apply(lambda x : remove_punct(x))
