def last_index_num(text):
    index = -1
    for charcter in text:
        if charcter.isdigit():
            index = text.index(charcter)
    return index

def del_text(row):
    s = str(row['price'])
    x = last_index_num(s)
    return s[0:x]
fiverr['price'] = fiverr.apply(del_text,axis=1)
fiverr['price']

