# Changes the mean and standard deviation of features to 0 and 1 respectively.
def standardize(df):
    for column in range(0, df.shape[1]):
        df[str(column)] = (df[str(column)] - np.mean(df[str(column)]))/np.std(df[str(column)]) 

standardize(train_x)

