def convert_str_with_commma2int(x):
    if type(x) is str:
        return int(x.replace(',',''))
    else:
        return x

def convert_str_with_percent2float(x):
    if type(x) == str:
        return float(x[:-1])
    else:
        return x


def str2float(x):
    return float(x)
