def simple_text_blob(inpdata1,inpdata2):
    from textblob import TextBlob
    if(inpdata2==2):
        return(inpdata1)
    elif(inpdata2==1):
        blob=TextBlob(inpdata1)
        inp=blob.tokenize()
        for word in inp:
            if(TextBlob(word).polarity>0):
                return(word)
    else:
        blob=TextBlob(inpdata1)
        inp=blob.tokenize()
        for word in inp:
              if(TextBlob(word).polarity<0):
                return(word)
