def downsample_df(df, target_col):
    positive_class = df[df[target_col] == 1]
    negative_class = (df[df[target_col] == 0]
                      .sample(n=positive_class.shape[0],
                              random_state=42))
    return pd.concat([positive_class, negative_class], axis=0)

