def police(speed:int ,is_birthday:bool):
    if is_birthday:
        speed -=5
    if speed < 60:
        return "No Ticket"
    elif 60<speed<80:
        return "Small Ticket"
    else:
        return "Big Ticket"

print("59,False ",police(59,False))
print("64,True  ",police(64,True))
print("82,False ",police(82,False))
print("82,True  ",police(82,True))
