# Create a Bubble base map
m_bubble_detail = folium.Map(location=[39.760982,10.00065], tiles='cartodbpositron', zoom_start=2)

def color_producer(val):
    if val <= 16:
        return 'blue'
    if val >= 16 and val<= 32:
        return 'yellow'
    if val >= 32 and val<= 48:
        return 'orange'
    if val >= 48 and val<= 64:
        return 'red'
    else:
        return 'black'

# Add a bubble map to the base map
for i in range(0,len(data)):
    Circle(
        location=[data.iloc[i]['Latitude'], data.iloc[i]['Longitude']],
        radius=20,
        color=color_producer(data.iloc[i]['Rank'])).add_to(m_bubble_detail)

# Display the map
m_bubble_detail
