image_dict = dict([(key, None) for key in range(len(flower_categories))])

def is_not_full_yet():
    for key, value in image_dict.items():
        if value is None:
            return True
    return False

for i, (image, label) in enumerate(train_dataset.as_numpy_iterator()):
