    if label in image_dict and image_dict[label] is None:
        plt.subplot(rows, cols, label + 1)
        plt.axis('off')
        plt.imshow(image)
        plt.title(flower_categories[label])
        image_dict[label] = True

    if not is_not_full_yet():
        break
