def norm_image(image):
    return (image - np.mean(image)) / np.std(image)

def min_max_scaling(image, new_min=-0.5, new_max=0.5):
    return new_min + (image - np.min(image)) * (new_max - new_min) /(np.max(image) - np.min(image))

