def Degree(mark):
    if mark>=90:
        return "AA"
    elif 90>mark>=85:
        return "BA"
    elif 85>mark>=80:
        return "BB"
    elif 80>mark>=75:
        return "CB"
    elif 75>mark>=65:
        return "CC"
    elif 65>mark>=58:
        return "DC"
    elif 58>mark>=50:
        return "DD"
    else:
        return "FF"

data["Mark_Degree"] = data.apply(lambda x: Degree(x["total_score"]),axis=1)
