# Removing the extra word
top['category'] = top['category'].apply(lambda x: ' '.join(x.split()[1:]))

# Converting all values to millions
def installs(x):
    if x[-1] == 'M':
        return(float(x[:-2]))
    else:
        return(float(x[:-2]) / 1000)

top['installs'] = top['installs'].apply(installs)
