def process_and_predict(file, name):
    im = Image.open(file)
    width, height = im.size
    if width == height:
        im = im.resize((256,256), Image.ANTIALIAS)
    else:
        if width > height:
            left = width/2 - height/2
            right = width/2 + height/2
            top = 0
            bottom = height
            im = im.crop((left,top,right,bottom))
            im = im.resize((256,256), Image.ANTIALIAS)
        else:
            left = 0
            right = width
            top = 0
            bottom = width
            im = im.crop((left,top,right,bottom))
            im = im.resize((256,256), Image.ANTIALIAS)

    ar = np.asarray(im)
    ar = ar.astype('float32')
    ar /= 255.0
    ar = ar.reshape(-1, 256, 256, 3)

    maxnum = np.argmax(model.predict(ar))
    if maxnum == 0:
        prediction = 'Bernese Mountain Dog'
    if maxnum == 1:
        prediction = 'Border Collie'
    if maxnum == 2:
        prediction = 'Chihuahua'
    if maxnum == 3:
        prediction = 'Corgi'
    if maxnum == 4:
        prediction = 'Dachshund'
    if maxnum == 5:
        prediction = 'Golden Retriever'
    if maxnum == 6:
        prediction = 'Jack Russell Terrier'
    if maxnum == 7:
        prediction = 'Pug'
    if maxnum == 8:
        prediction = 'Siberian Husky'

    print(name + ' is a ' + prediction)
    return im
