def getNames(arr):
    names = []
    counter = 0
    for i in range(len(arr)):
        if arr[i] ==",":
            names.append(arr[counter:i])
            counter=i+2
    for i in range(len(arr)-1,0,-1):
        if arr[i]==",":
            names.append(arr[2+i:])
            break
    return names

