def getMonth(arr):
    sayac = 0
    month = []
    for i in range(len(arr)):
        if arr[i]=="?":
            month.append(arr[i])
        else:
            for j in range(len(arr[i])):
                if j>0:
                    if arr[i][j]==" ":
                        month.append(arr[i][0:j])
                        break
    return month

