class MyModel:
  def __init__(self, learning_rate=0.05):
    self.learning_rate = learning_rate

  def sigmoid(self, y_hat):
    return 1 / (1 + np.e**(-y_hat))

  def fit(self, X, y, epochs=25):
    self.w = np.random.rand(X.shape[1])
    for epoch in range(epochs):
      y_hat = X @ self.w
      diff = 0
      for i, x in enumerate(X):
        diff += x * (y_hat[i] - y[i])
      self.w -= self.learning_rate * diff

  def predict(self, X):
    y_hat = X @ self.w
    return [1 if p >= 0.5 else 0 for p in self.sigmoid(y_hat)]

