# Using subplots to create multiple charts
fig2 = plt.figure(
    FigureClass=Waffle,
    plots={
        '411': {                              #refer matplotlib subplot grids, '411' means 4 x 1 grid, first subplot
            'values': df['Army'],
            'labels': list(df.Year),
            'legend': {
                'loc': 'upper left',
                'bbox_to_anchor': (1.05, 1)
            },
            'title': {
                'label': 'Army',
                'loc': 'left'
            }
        },
        '412': {
            'values': df['Navy'],
            'labels': list(df.Year),
            'legend': {
                'loc': 'upper left',
                'bbox_to_anchor': (1.05, 1)
            },
            'title': {
                'label': 'Navy',
                'loc': 'left'
            }
        },
        '413': {
            'values': df['Air Force'],
            'labels': list(df.Year),
            'legend': {
                'loc': 'upper left',
                'bbox_to_anchor': (1.05, 1)
            },
            'title': {
                'label': 'Air force',
                'loc': 'left'
            }
        },
        '414': {
            'values': df['Total']/10,   #scaling purpose to fit 
            'labels': list(df.Year),
            'legend': {
                'loc': 'upper left',
                'bbox_to_anchor': (1.05, 1)
            },
            'title': {
                'label': 'Total',
                'loc': 'left'
            }
        },

    },
    rows=5,
    figsize=(11, 9)
)
