        return row['age']/10
    elif row['club']  == 'Manchester United':
        return row['age']/10
    elif row['club'] == 'Liverpool':
        return row['age']/10
    elif row['club'] == 'Tottenham Hotspur':
        return row['age']/10
    elif row['club'] == 'Arsenal':
        return row['age']/10
    elif row['club'] == 'Chelsea':
        return row['age']/10
