# Function to remove the puctuation marks from my string
punctuations = '''!()-[]{};:'"\,<>./?@#$%^&*_~'''
def remove_punctuation(my_str):
    no_punct = ""
    for char in my_str:
       if char not in punctuations:
           no_punct = no_punct + char

    # return the unpunctuated string
    return no_punct

