# define a function to map item prioritites on the data

def category(x,y):
    if ((x>=300000) & (y>=1)):
        return 'Strategic'
    if ((x>=300000) & (y<1)):
        return 'Leverage'
    if ((x<300000) & (y>=1)):
        return 'Critical'
    if ((x<300000) & (y<1)):
        return 'Routine'
