class _TextData(TextData):
    def to_model(self, m, opt_fn, **kwargs):
        model = TextModel(to_gpu(m))
        return RNN_Learner(self, model, opt_fn=opt_fn, **kwargs)

    def get_model(self, opt_fn, max_sl, bptt, emb_sz, n_hid, n_layers, dropout, **kwargs):
        lm_kwargs = {k:v for k,v in kwargs.items() if k not in LEARNER_KWARGS}
        m = get_rnn_classifer(bptt, max_sl, self.c, self.nt,
              layers=[emb_sz*3, self.c], drops=[dropout],
              emb_sz=emb_sz, n_hid=n_hid, n_layers=n_layers, pad_token=self.pad_idx, **lm_kwargs)
        learner_kwargs = {k:v for k,v in kwargs.items() if k in LEARNER_KWARGS}
        return self.to_model(m, opt_fn, **learner_kwargs)
