        original = pd.read_csv(data_path + ticker + file_suffix)
        dates = original['Date'].map(lambda t: datetime.strptime(t, '%Y-%m-%d')).to_numpy()
        prices = original['Open'].to_numpy()
        return cls(dates, prices)

