# binning distance_from_home
def dist_home(n):
    if n <= 10:
        return 'Near'
    elif n > 10 and n <=25:
        return 'Far'
    elif n > 25:
        return 'Very far'


df['dist_home_bins'] = df['DistanceFromHome'].apply(lambda x: dist_home(x))
