import pandas as pd
import numpy as np
import matplotlib.pyplot as plt
import seaborn as sns
import pycountry_convert as pc
import warnings
warnings.filterwarnings('ignore')
data=pd.read_csv('../input/kaggle-survey-2021/kaggle_survey_2021_responses.csv')

keys=data['Q3'].unique()
values=[]
for k in keys:
    if k=='Iran, Islamic Republic of...':
        values.append('Iran')
    elif k=='United Kingdom of Great Britain and Northern Ireland':
        values.append('UK')
    elif k=='I do not wish to disclose my location':
        values.append('Other')
    elif k=='United States of America':
        values.append('USA')
    elif k=='United Arab Emirates':
        values.append('UAE')
    elif k=='Hong Kong (S.A.R.)':
        values.append('Hong Kong')
    else:
        values.append(k)


country_dict = {}
i=0
for k in keys:
    country_dict[k]=values[i]
    i+=1

data['Q3']=data['Q3'].apply(lambda x : country_dict[x])

def get_continet_by_country(country):

 try:
     country_code = pc.country_name_to_country_alpha2(country, cn_name_format="default")
     continent_name = pc.country_alpha2_to_continent_code(country_code)
 except:
    if country=='UK':
        continent_name='EU'
    elif country=='UAE':
        continent_name='AS'
    else:
      continent_name='Not Found'
 return continent_name

