def findNull(df):
    print("Column\t\t\tNull Percentage\t\t\tNull Records")
    for col in df.columns:
        null_sum = df[col].isnull().sum()
        print(f"{col}\t\t\t{null_sum/len(df)*100}%\t\t\t{null_sum} Null Records")

