result = []
with open('generated-pantun.txt', 'r') as f:
    lines = f.readlines()
    for line in lines:
        if line.startswith('<BOS>'):
            result.append(line[5:].replace(r'\\n', '\n'))

for idx, pantun in enumerate(result):
    print(' ===========\n   Pantun {}\n ===========\n'.format(idx))
    print(pantun)
