#Creating a function for calculating per day increase of any feature. This takes list as an argument.

def per_day_rise(x):
    per_day_rise = []

    for i in range(len(x)):
        if i==0:
            per_day_rise.append(0)
        else:
            per_day_rise.append(x[i]-x[i-1])

    return per_day_rise
