def scheduler(epoch):
    lr = 0.001
    if epoch < 10:
        return lr
    elif epoch < 20:
        return lr / 3
    elif epoch < 30:
        return lr / 10
    elif epoch < 50:
        return lr / 5
    elif epoch < 60:
        return lr / 10
    elif epoch < 70:
        return lr / 50
    else:
        return lr / 100

