def procesar_dato():    
    try:
        with open("archivo_entrada.csv") as fp:
            number = fp.read().strip()
            print(f"La división de 50 por {number} es {50 / int(number)}")
    except FileNotFoundError:
        print ("El archivo no existe.")
    except ZeroDivisionError:
        print ("El número ingresado es igual a 0.")
    except Exception as e:
        print ("Error inesperado: {e}")
        raise

try:
    procesar_dato()
except Exception as e:
    print (f"Error: {e}")

