def es_par(dato):
    try:
        if int(dato) % 2 != 0:
            raise Exception("El número ingresado debe ser par.")
    except ValueError:
        print ("El valor ingresado debe ser un número")

dato = input("Ingrese el un número par: ")

try:
    es_par(dato)
    print("El número es correcto")
except Exception as e:
    print (f"Error: {e}")

